mapper.select:

SELECT
b.*
FROM
lyj_requirement b
where 1=1

    and lyj_requirement_state=#{state}



    and (
    b.lyj_requirement_name like #{pattern}
    or b.lyj_requirement_description like #{pattern}
    or b.lyj_requirement_city like #{pattern}
    or b.lyj_requirement_area like #{pattern}
    or b.lyj_requirement_street like #{pattern}
    or b.lyj_requirement_rawaddress like #{pattern}
    or b.lyj_requirement_connection like #{pattern}
    )


SELECT
count(1)
FROM
lyj_requirement b
where 1=1

    and lyj_requirement_state=#{state}



    and (
    b.lyj_requirement_name like #{pattern}
    or b.lyj_requirement_description like #{pattern}
    or b.lyj_requirement_city like #{pattern}
    or b.lyj_requirement_area like #{pattern}
    or b.lyj_requirement_street like #{pattern}
    or b.lyj_requirement_rawaddress like #{pattern}
    or b.lyj_requirement_connection like #{pattern}
    )


SELECT
b.*
FROM
lyj_requirementtype_relation a left join
lyj_requirement b
on a.lyj_requirement_id=b.lyj_requirement_id
where a.lyj_requirement_typeid=#{typeId}

    and lyj_requirement_state=#{state}



    and (
    b.lyj_requirement_name like #{pattern}
    or b.lyj_requirement_description like #{pattern}
    or b.lyj_requirement_city like #{pattern}
    or b.lyj_requirement_area like #{pattern}
    or b.lyj_requirement_street like #{pattern}
    or b.lyj_requirement_rawaddress like #{pattern}
    or b.lyj_requirement_connection like #{pattern}
    )


SELECT
count(1)
FROM
lyj_requirementtype_relation a left join
lyj_requirement b
on a.lyj_requirement_id=b.lyj_requirement_id
where a.lyj_requirement_typeid=#{typeId}

    and lyj_requirement_state=#{state}



    and (
    b.lyj_requirement_name like #{pattern}
    or b.lyj_requirement_description like #{pattern}
    or b.lyj_requirement_city like #{pattern}
    or b.lyj_requirement_area like #{pattern}
    or b.lyj_requirement_street like #{pattern}
    or b.lyj_requirement_rawaddress like #{pattern}
    or b.lyj_requirement_connection like #{pattern}
    )


SELECT
b.*
FROM
lyj_requirement b
where 1=1

    and b.lyj_requirement_city=#{city}


    and b.lyj_requirement_street=#{street}


    and str_to_date(#{firstDate},'%Y-%m-%d %H:%i:%s') < b.lyj_requirement_begindatetime and
    str_to_date(#{secondDate},'%Y-%m-%d %H:%i:%s') > b.lyj_requirement_begindatetime


    and (
    b.lyj_requirement_name like #{pattern}
    or b.lyj_requirement_description like #{pattern}
    or b.lyj_requirement_city like #{pattern}
    or b.lyj_requirement_area like #{pattern}
    or b.lyj_requirement_street like #{pattern}
    or b.lyj_requirement_rawaddress like #{pattern}
    or b.lyj_requirement_connection like #{pattern}
    )


    order by b.lyj_requirement_begindatetime


    order by b.lyj_requirement_begindatetime desc


SELECT
b.*
FROM
lyj_requirement b left join
lyj_requirementtype_relation a
on a.lyj_requirement_id=b.lyj_requirement_id
where a.lyj_requirement_typeid=#{typeId}

    and b.lyj_requirement_city=#{city}


    and b.lyj_requirement_street=#{street}


    and str_to_date(#{firstDate},'%Y-%m-%d %H:%i:%s') < b.lyj_requirement_begindatetime and
    str_to_date(#{secondDate},'%Y-%m-%d %H:%i:%s') > b.lyj_requirement_begindatetime


    and (
    b.lyj_requirement_name like #{pattern}
    or b.lyj_requirement_description like #{pattern}
    or b.lyj_requirement_city like #{pattern}
    or b.lyj_requirement_area like #{pattern}
    or b.lyj_requirement_street like #{pattern}
    or b.lyj_requirement_rawaddress like #{pattern}
    or b.lyj_requirement_connection like #{pattern}
    )


    order by b.lyj_requirement_begindatetime


    order by b.lyj_requirement_begindatetime desc

