mapper.select:
SELECT lyj_requirement_city FROM lyj_requirement where lyj_requirement_city is not NULL and lyj_requirement_city!=''
and lyj_requirement_state=#{state}
GROUP BY lyj_requirement_city

SELECT lyj_requirement_street FROM lyj_requirement where lyj_requirement_street is not NULL and lyj_requirement_street!=''
and lyj_requirement_state=#{state}
GROUP BY lyj_requirement_street
